<?phpif( isset($field_data) and is_array($field_data) and sizeof($field_data)>0 ){?>          <tr class="glav">            <td align="right"><?php echo output($field_data[0]['name'])?>:&nbsp;<?php            if( intval($field_data[0]['req'])>0)            {            ?><span style="color: red;">*</span><?php            }            ?><?PHP            if( isset($field_data[0]['descr']) and !empty($field_data[0]['descr']) )            {            ?><br /><span><small><?php echo output($field_data[0]['descr']);?></small></span><?php            }            ?></td>            <td><input value="<?php            $current_value = '';            if( isset($field_data[0]['val']) and !empty($field_data[0]['val']) )            {                $current_value = output($field_data[0]['val']);            }            else            {                $current_value = output($field_data[0]['def_value']);            }            echo $current_value;            ?>" type="checkbox"<?php			if( isset($user_value) and !empty($user_value)             //and strcmp(trim($user_value),trim($current_value))==0             )            {                echo " checked=true ";            }            elseif( (!isset($user_value) or empty($user_value)) and isset($field_data[0]['def_value']) )            {                if( strcmp(trim($field_data[0]['val']),trim($field_data[0]['def_value']))==0 )                {                    echo " checked=true ";                }            }            ?> id="user_registration_add_field_<?php echo intval($field_data[0]['id']);?>" name="add_field_<?php echo intval($field_data[0]['id']);?>" /></td>          </tr><?php}?>
